package capsule_11_bySweety;

public class Sum_of_all_1st_5Whole_no {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Whole no and its sum 
		
				int str = 0;
				int end = 4;
				int sum = 0;

				while (str <= end) {
				    
				        sum = sum + str;
				        str++;
				}

				System.out.println(sum+ "  Sum of all 1st five whole no ");
	}

}
