package capsule_12_bySweety;

public class Factorial_of_a_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int str =1;
		int end =9 ;
		int fact = 1;
		
		while (str<=end)
		{
			fact = fact*str ;
			str++;
			
		}
		System.out.println(" Factorial of a number  "+ end +" is " + fact );
		
		
		
	}

}
