package capsule_16_bySweety;

public class Swap_2no_without_using_3rd_variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
          int a = 5 ;
          int b = 10 ;
          System.out.println("befor  swaping the value -" +" a val "+a);
          System.out.println("befor  swaping the value -" +" b val "+b);
          
          a = a+b;
          b= a-b;
          a= a-b ;
          System.out.println();
          System.out.println("after  swaping the value -" +" a val "+a);
          System.out.println("after  swaping the value -" +" b val "+b);
          
          
          
          
		
	}

}
