package capsule_16_bySweety;

public class Swapping_2no_3rd_variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a= 9;
		int b= 1;
		System.out.println( " before swaping the value "+"a  = "+a  +" | b = "+ b);
		
		int c = a;
		a= b ;
		b=c ;
		System.out.println(" after swaping the value "+"a  = "+a  +" | b = "+ b);
		
		
		
	}

}
