package capsule_21_bySweety;

public class Dynamic_Arr_without_enter_val {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int arr [] =new int [5];
		for(int i=0;i< arr.length ;i++)
		{
			arr[i]=i+1;
			
		}
		for(int i=0;i< arr.length ;i++)
		{
			System.out.println(arr[i]);
			
		}
		
	}

}
