package capsule_21_bySweety;

public class Table_2_Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int arr []= new  int [10];
		int tb = 2 ;
		for(int i =0 ; i < arr.length ;i++ )
		{
			arr[i]= (i+1)*tb ;
			
		}
		// print 
		for(int i =0 ; i<arr.length ;i++ )
		{
			System.out.println(arr[i] +" ");
			
		}

		
	}

}
