package capsule_5_bySweety;

public class Even_Odd_Using_if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num =89 ;
		
		if(num%2==0)
		{
			System.out.println(" its a even no ");
		}
		else
		{
			System.out.println(" its a odd no ");
		}
		
		
		
		
		
		
	}

}
