public class Greatest_2_no_using_if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=90;
		int b=400;
		
		if(a>b)
		{
			System.out.println(a +"a  Is the greatest of 2 no ");
		}
		else
		{
			System.out.println(b + " b Is the greatest of 2 no ");
		}
	}

}
