package sweety_program_practic;

public class Even_odd_concept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          //Even_odd_concept in if else condition 
		
		int num = 68;
		
		if(num%2==0)
		{
			System.out.println(num +"  its a even no ");
		}
		else
		{
			System.out.println(num +"  its not an even no ");
		}
	}

}
