package sweety_program_practic;

public class Factorial_of_number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
		int num=4;
		int fact=1;
		 for(int i =1;i<=num;i++)
		 {
			fact*=i;
			
		 }
		 System.out.println(" Factorial of number  "+num + " is  :  "+fact);
			 
	}

}
