package sweety_program_practic;

public class Numbers_in_words {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=1;i<=9;i++)
		{
			switch(i)
			{
			case 0: System.out.println(i+ " in words zero");
			        break ;
			        
			        
			case 1: System.out.println(i+ " in words one");
	                break ;

			case 2: System.out.println(i+ " in words two");
	               break ;

			case 3: System.out.println(i+ " in words three");
	               break ;

			case 4: System.out.println(i+ " in words four");
	                break ;

			case 5: System.out.println(i+ " in words five");
	                break ;

			case 6: System.out.println(i+ " in words six");
	                 break ;

			case 7: System.out.println(i+ " in words seven");
	                 break ;

			case 8: System.out.println(i+ " in words eight");
	                break ;
	        
			case 9: System.out.println(i+ " in words nine");
	                break ;
            
			default : System.out.println(i+ " default value ");
	       

			}
		}
	}

}
