package sweety_program_practic;

public class While_loop_10_to_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// st = 10 , end =1

		int st = 10;
		int end = 1;
		
		while(st>=end)// 10>=1   9>=1  8>=1  7>=1  ...... 
		{
			System.out.println(st);// 10 9 8 7
			st--;// 9 8 7 6
		}
		
	}

}
